<form action="cargo.php?menu=ships&action=add" method="post">	Name: <br><input name="name" class="edit_adding baseFont" type="edit" placeholder="Name" required>	Surname: <br><input name="surname" class="edit_adding baseFont" type="edit" placeholder="Surname" required>	Address: <br><input name="address" class="edit_adding baseFont" type="edit" placeholder="Address" required>	Country: <br><select name="country">				<option>arabusy pojebusy				<option>zabojady				<option>zoltki				<option>szwaby			  </select>	<br><br>	<input class="button baseFont menu_button" type="submit" value="Add">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="add_toggle()"></form>