<?php    if( isset( $_GET['id'] ) )     {        $cargo = get_cargo_by_id($_GET['id']);                if($cargo==NULL)            return;        echo "<table class=\"stuff_info\">		<tr>			<td class=\"stuff_no\" colspan=\"2\">				Cargo ID: $cargo->id			</td></tr><tr><td width=\"50%\">				Commodity			</td><td>				$cargo->commodity			</td></tr><tr><td>				Cargo Type			</td><td>				$cargo->type			</td></tr><tr><td>				Amount			</td><td>				$cargo->amount			</td></tr><tr><td>				Value			</td><td>				$cargo->value			</td></tr><tr><td>				Customs duty value			</td><td>				$cargo->duty			</td></tr><tr><td>				Owner			</td><td>				$cargo->sender			</td></tr>					</table><br>			<hr><br>";            if( isset( $_GET['status'] ) )             {                if( $_GET['status'] == 'accepted' )                 {                                    echo 'Duty control result - accepted.';                }                 elseif( $_GET['status'] == 'suspended' )                 {                                    echo 'Duty control result - cargo unload suspended.';                }                 elseif( $_GET['status'] == 'confiscated' )                 {                                            echo 'Duty control result - cargo confiscated.';                }            }	if( !isset( $_GET['dutyControl'] ) )         {            if( !isset( $_GET['done'] ) && !isset( $_GET['newCharge'] ) )             {                echo '<a href="?menu=cargo&id=' . $_GET['id'] . '&dutyControl="><input class="button baseFont" type="button" value="Duty control"></a><br>';                            }	}         else         {            echo '<form action="?menu=cargo&id=' . $_GET['id'] . '&done=status" method="post">';                                    echo '	<h2>Additional charge:</h2>';            $types=get_payment_types();            echo '	Type: <br><select name="charge_type">';                        foreach ($types as $type)                echo "<option value=\"$type\">$type</option>";            echo '	</select><br>';                        echo '	<br><input name="charge" type="text" placeholder="Charge" pattern="^[0-9]+$"><br/>';                        echo '<h2>Remarks:</h2><textarea name="remarks" cols="25" rows="5" placeholder="Remarks"></textarea>';             echo "<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\">                <tr>                    <td>				                        <input class=\"button baseFont\" type=\"submit\" name=\"accepted\" value=\"Accept\" />                    </td>                    <td>                        <input class=\"button baseFont\" type=\"button\" value=\"Refuse\" onClick=\"refuse()\">                    </td>                </tr>                <tr>                    <td>                            <!-- leave empty -->                    </td>                    <td>                                                            <input id=\"refused1\" style=\"visibility: hidden\" class=\"button baseFont\" type=\"submit\" name=\"suspended\" value=\"Suspend\" />                        <input id=\"refused2\" style=\"visibility: hidden\" class=\"button baseFont\" type=\"submit\" name=\"confiscated\" value=\"Confiscate\" />                    </td>                </tr>                </table></form>";        }        if( isset( $_GET['done'] ) )         {            if( $_GET['done'] == 'status' && isset( $_POST['remarks'] )  )             {                                    $remarks=$_POST['remarks'];                         if(!$remarks)                {                    echo "none";                    $remarks='';                    echo "$remarks";                }                if (isset( $_POST['accepted'] ))                {                                        duty_control_accept($_GET['id'],$remarks);                }                elseif (isset( $_POST['suspended'] ))                {                    duty_control_suspend($_GET['id'],$remarks);                }                elseif (isset( $_POST['confiscated'] ))                {                    duty_control_confiscate($_GET['id'],$remarks);                }                    if(isset($_POST['charge']) && !empty($_POST['charge']) && isset($_POST['charge_type']))                {                                        $value=$_POST['charge'];                    $type=$_POST['charge_type'];                    $contractor=$cargo->owner_id;                    add_payment($contractor,$value,$type);                                    }            }                     }            } ?>