<div>		<form action="?menu=history&action=show" method="post">                <div style="float: left; line-height: 21px;">		Date:				<input name="from" style="width: 70px" class="ship_edit baseFont" type="text" 					pattern="^[0-9]{4}-[0-9]{2}-[0-9]{2}$" placeholder="From">				<input name="to" style="width: 70px" class="ship_edit baseFont" type="text" 					pattern="^[0-9]{4}-[0-9]{2}-[0-9]{2}$" placeholder="To"><br>		                <label><input name="status" value="all" type="radio">All</label><br>                <label><input name="status" value="positive" type="radio">Accepted</label><br>                <label><input name="status" value="suspended" type="radio">Suspended</label><br>                <label><input name="status" value="confiscated" type="radio">Confiscated</label>                </div>	<input class="button baseFont add" type="submit" value="Show">	</form></div><br><div class="duty_history_list overf">	<?php				            if(isset($_POST['from']))                $from=$_POST['from'];            else                $from='';            if(isset($_POST['to']))                $to=$_POST['to'];            else                $to='';            if(isset($_POST['status']))                $status=$_POST['status'];            else                $status='all';            $controls=select_controls_history($from, $to,$status);            if($controls!=NULL)                foreach($controls as $control)                {					echo "<table class=\"item\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\">";					echo "<tr><td class=\"name left_column\" onClick=\"window.location.href='?menu=history&id=$control->id'\">$control->date</td></tr>";					echo "<tr><td class=\"level\">$control->commodity (Amount: $control->amount)</td></tr>";					echo "</table><br>";					                }	?>	</div>