<form action="?menu=payments&action=add" method="post">        Contractor:<br/>	<select name="contractor" class="baseFont select">			<?php										$contractors=get_contractors();					foreach($contractors as $contractor)																	echo "<option value=\"$contractor->id\">$contractor->name</option>";								?>	</select><br/>	        Type:<br/>	<select name="type" class="baseFont select">			<?php										$types=get_payment_types();					foreach($types as $type)																	echo "<option value=\"$type\">$type</option>";								?>	</select><br />	Charge: <br><input name="value" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$" required><br><br>	<input class="button baseFont menu_button" type="submit" value="Add">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="add_toggle()"></form>