<form name="edit_form" action="?menu=payments&action=edit&id=ID" method="post">	Contractor: <br><input name="contractor" class="edit_adding baseFont" type="edit" placeholder="Name Surname">	Type: <br><select name="type">				<option>type			  </select><br>	Amount: <br><input name="amount" class="edit_adding baseFont" type="edit" placeholder="100" pattern="^[0-9]+$"><br><br>	<input class="button baseFont menu_button" type="submit" value="Edit">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="edit_toggle()"></form>