<form name="edit_form" action="admin_system.php?menu=docks&action=edit&id=ID" method="post">	Id: <br><input name="id" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$"><br>		Fee: <br><input name="fee" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$"><br>	Max Ship Length: <br><input name="length" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$"><br>	Max Ship Width: <br><input name="width" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$"><br>	Max Ship Height: <br><input name="height" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$"><br><br>	<input class="button baseFont menu_button" type="submit" value="Edit">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="edit_toggle()"></form>