<form name="edit_form" action="admin_system.php?menu=warehouses&action=edit&id=ID" method="post">	Name: <br><input name="name" class="edit_adding baseFont" type="edit" required><br>		Capacity: <br><input name="capacity" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$" required><br>	Fee: <br><input name="fee" class="edit_adding baseFont" type="edit" pattern="^[0-9]+$" required><br><br>	<input class="button baseFont menu_button" type="submit" value="Edit">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="edit_toggle()"></form>