<form action="admin_system.php?menu=ships&action=add" method="post" enctype="multipart/form-data">	Name: <br><input name="name" class="edit_adding baseFont" type="edit" onBlur="isEmpty( this )"><br>	Type: <br><input name="type" class="edit_adding baseFont" type="edit" onBlur="isEmpty( this )"><br>	Displacement: <br><input name="displacement" class="edit_adding baseFont" type="edit" onBlur="validateEdit( this )"><br>	Capacity: <br><input name="capacity" class="edit_adding baseFont" type="edit" onBlur="validateEdit( this )"><br>	Length: <br><input name="length" class="edit_adding baseFont" type="edit" onBlur="validateEdit( this )"><br>	Width: <br><input name="width" class="edit_adding baseFont" type="edit" onBlur="validateEdit( this )"><br>	Height: <br><input name="height" class="edit_adding baseFont" type="edit" onBlur="validateEdit( this )"><br>	Image: <br><input name="image" class="edit_adding baseFont" type="file"><br><br>	<input class="button baseFont menu_button" type="submit" value="Add" onClick="return validate( 'name|type|displacement|capacity|length|width|height' )">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="add_toggle()"></form>