<form name="edit_form" action="admin_system.php?menu=users&action=edit&id=ID" method="post" enctype="multipart/form-data">	Name: <br><input name="name" class="edit_adding baseFont" type="edit" onBlur="isEmpty( this )"><br>	Surname: <br><input name="surname" class="edit_adding baseFont" type="edit" onBlur="isEmpty( this )"><br>	Level: <br><input name="level" class="edit_adding baseFont" type="edit" onBlur="isEmpty( this )"><br>	Description: <br><textarea name="description" cols="10" rows="5" class="txt_area edit_adding baseFont" onBlur="isEmpty( this )"></textarea><br>	Image: <br><input name="image" class="edit_adding baseFont" type="file"><br><br>	<input class="button baseFont menu_button" type="submit" value="Edit" onClick="return validate( 'name|surname|level|description' )">	<input class="button baseFont menu_button" type="button" value="Cancel" onClick="edit_toggle()"></form>